"use strict";

import {
  BaseEntity,
  Collection,
  Entity,
  OneToMany,
  PrimaryKey,
  Property,
} from "@mikro-orm/core";
import { Chart } from "./Chart.entity.js";
import { v4 } from "uuid";

@Entity()
export class Song extends BaseEntity {
  @PrimaryKey()
  uuid = v4();

  // Name of the song, using the package name as a reference.
  @Property()
  name!: string;

  // The charts in this song package.
  @OneToMany(() => Chart, (chart) => chart.song)
  charts = new Collection<Chart>(this);

  // Package path, generated by us.
  @Property()
  packagePath?: string;
}
